    }

    // reducer Ìï®ÏàòÍ∞Ä Îã§Ïùå ActionÏùÑ Î∞òÌôòÌïòÎäî ÌòïÌÉúÏòÄÎã§Í≥† Í∞ÄÏ†ï
    abstract suspend fun reducer(action: ACTION): CaAction? // ÏòàÏãú: Î∞òÌôò ÌÉÄÏûÖÏù¥ Îã§Ïùå Action
