 mutate {
  remove_field => ["@timestamp", "@version", "path", "host"]
 }


 csv {
  columns => ["lat", "lon"]
  add_field => {"point" => "%{lat}"}
  add_field => {"point" => "%{lon}"}
  convert => {"point" => "float"}
 }
}




{
    "message" => "37.48854368,126.9094842\r",
