interface Navigator {

    suspend fun <T : ActivityRoute> navigate(
        activityRoute: KClass<T>,
        argumentMap: Map<String, Parcelable> = emptyMap(),
    )

    suspend fun navigate(
        navigationRoute: NavigationRoute,
        saveState: Boolean = false,
    )

    suspend fun navigateBack()
}


