        const pos = match.index;

        if (!detectedPositions.has(pos)) {
          watermarks.count++;
          watermarks.types.add(pattern.type);
          detectedPositions.add(pos);

          const charCode = match[0].codePointAt(0);
          const unicode = `U+${charCode.toString(16).toUpperCase().padStart(4, '0')}`;

          watermarks.positions.push({
            type: pattern.type,
            position: pos,
            char: match[0],
            unicode: unicode,
