:root {
  --fashion-1: #593C47;
  --fashion-2: #F2E63D;
  --fashion-3: #F2C53D;
  --fashion-4: #F25C05;
  --fashion-5: #F24405;
  --text-dark: #334155;
  --text-section: #1e40af;
  --text-subtitle: #475569;
}

* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

