.header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 30px;
  border-bottom: 2px dashed var(--fashion-1);
  padding-bottom: 15px;
}

.title {
  font-size: 32px;
  font-weight: bold;
  /* font-family Ï†úÍ±∞ÌïòÏó¨ Î∏îÎ°úÍ∑∏ Í∏∞Î≥∏ Ìè∞Ìä∏ ÏÇ¨Ïö© */
  background: linear-gradient(to right, var(--fashion-1), var(--fashion-5));
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  position: relative;
  display: inline-block;
}

.title::after {
  content: "";
  position: absolute;
  bottom: -5px;
  left: 0;
  width: 100%;
  height: 3px;
  background: linear-gradient(to right, var(--fashion-3), var(--fashion-4));
  border-radius: 2px;
}

.date {
  font-size: 14px;
  color: var(--text-subtitle);
}

.intro {
  position: relative;
  padding: 20px;
  margin-bottom: 30px;
  background-color: #ffffffd0;
  border-radius: 12px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  backdrop-filter: blur(10px);
}

.intro::before {
  content: "‚ú®";
  position: absolute;
  top: -15px;
  left: 20px;
  font-size: 24px;
}

.intro-text {
  font-size: 16px;
  line-height: 1.6;
}

.grid-container {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 20px;
  margin-bottom: 30px;
}

.card {
  position: relative;
  background-color: white;
  border-radius: 12px;
  padding: 20px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease, box-shadow 0.3s ease;
  overflow: hidden;
}

.card:hover {
  transform: translateY(-5px);
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.15);
}

.card::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 5px;
  height: 100%;
  background: linear-gradient(to bottom, var(--fashion-2), var(--fashion-4));
  border-radius: 4px 0 0 4px;
}

.section-title {
  display: flex;
  align-items: center;
  font-size: 18px;
  font-weight: bold;
  color: var(--text-section);
  margin-bottom: 15px;
  padding-bottom: 8px;
  border-bottom: 2px dashed #e2e8f0;
}

.section-title i {
  margin-right: 8px;
  font-size: 20px;
  background: var(--fashion-3);
  color: white;
  width: 30px;
  height: 30px;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 50%;
}

.section-content {
  font-size: 14px;
  line-height: 1.4;
}

.highlight {
  position: relative;
  display: inline-block;
  padding: 0 2px;
  z-index: 1;
}

.highlight::before {
  content: "";
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 7px;
  background-color: var(--fashion-2);
  z-index: -1;
  opacity: 0.7;
}

.tool-icon {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  width: 24px;
  height: 24px;
  background-color: var(--fashion-1);
  color: white;
  border-radius: 50%;
  margin-right: 5px;
  font-size: 12px;
}

.feature-list {
  margin: 15px 0;
  padding-left: 20px;
  list-style-type: none;
}

.feature-list li {
  position: relative;
  margin-bottom: 8px;
  padding-left: 20px;
}

.feature-list li::before {
  content: "‚úì";
  position: absolute;
  left: 0;
  color: var(--fashion-4);
  font-weight: bold;
}

.prompt-example {
  background-color: #f1f5f9;
  border-left: 3px solid var(--fashion-3);
  padding: 10px;
  margin: 10px 0;
  /* font-family Ï†úÍ±∞ÌïòÏó¨ Î∏îÎ°úÍ∑∏ Í∏∞Î≥∏ Ìè∞Ìä∏ ÏÇ¨Ïö© */
  font-size: 16px;
  color: #334155;
  position: relative;
}

.prompt-example::before {
  content: " ";
  position: absolute;
  top: -10px;
  right: 10px;
  font-size: 20px;
}

.arrow {
  position: absolute;
  width: 40px;
  height: 20px;
}

.arrow.right {
  transform: rotate(-30deg);
  right: -20px;
  top: 50%;
}

.arrow.down {
  transform: rotate(90deg);
  bottom: -20px;
  left: 50%;
}

.qa-section {
  background-color: white;
  border-radius: 12px;
  padding: 20px;
  margin-top: 30px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
}

.qa-title {
  font-size: 18px;
  font-weight: bold;
  color: var(--text-section);
  margin-bottom: 15px;
  padding-bottom: 8px;
  border-bottom: 2px dashed #e2e8f0;
  display: flex;
  align-items: center;
}

.qa-title::before {
  content: " ";
  margin-right: 10px;
}

.qa-item {
  margin-bottom: 15px;
}

.question {
  font-weight: bold;
  margin-bottom: 5px;
  color: var(--fashion-5);
}

.answer {
  padding-left: 20px;
}

.footer {
  text-align: center;
  margin-top: 30px;
  padding-top: 15px;
  border-top: 2px dashed var(--fashion-1);
  font-size: 12px;
  color: var(--text-subtitle);
}

@media (max-width: 768px) {
  .grid-container {
    grid-template-columns: 1fr;
  }
}

.hand-drawn {
  position: relative;
  color: var(--fashion-1);
  font-weight: bold;
}

.hand-drawn::after {
  content: "";
  position: absolute;
  bottom: -2px;
  left: 0;
  width: 100%;
  height: 2px;
  background: repeating-linear-gradient(to right, var(--fashion-4), var(--fashion-4) 2px, transparent 2px, transparent 4px);
}

.connection-line {
  position: absolute;
  border: 2px dashed var(--fashion-3);
  z-index: -1;
}

.emoji-bullet {
  display: inline-block;
  width: 20px;
  text-align: center;
  margin-right: 5px;
}




