function removeWatermark(text) {
  let cleaned = text
    // Ïù¥Î™®ÏßÄ Î≥ÄÌòï ÏÑ†ÌÉùÏûê Ï†úÍ±∞
    .replace(/[\uFE00-\uFE0F]/g, '')
