}

// Composable View
@Composable
fun SomeScreen() {
  // CompositionLocalÏùÑ ÌÜµÌï¥ ActionDispatcher ÌöçÎìù
  val actionDispatcher = LocalActionDispatcher.current
  var textState by remember { mutableStateOf("") }
  var switchState by remember { mutableStateOf(false) }

  Column {
      Button(onClick = { actionDispatcher.dispatch(MyScreenAction.ButtonClick) }) {
          Text("Click Me")
      }
      TextField(
          value = textState,
          onValueChange = {
              textState = it
              actionDispatcher.dispatch(MyScreenAction.TextTyped(it))
          }
      )
      Switch(
          checked = switchState,
          onCheckedChange = {
              switchState = it
              actionDispatcher.dispatch(MyScreenAction.SwitchChanged(it))
          }
      )
  }
}

// ViewModel
class SomeViewModel(
    private val flowCaActionStream: FlowCaActionStream // Action Ïä§Ìä∏Î¶º Ï£ºÏûÖ
) : CaViewModel<MyScreenAction>(flowCaActionStream, MyScreenAction::class) { // ÏàòÏã†Ìï† Action ÌÉÄÏûÖ ÏßÄÏ†ï

